import React from "react";
import Select from "react-select";

const customStyles = {
    option: (provided) => ({
        ...provided,
        textAlign:"left"
    }),
    control: (base, state) => ({
        ...base,
        border: state.isFocused ? "1px solid #1C4980" : "1px solid #eeeeee",
        "&:hover": {
            boxShadow: "none",
            outline: " none",
        },
        boxShadow: "none",
        outline: " none",
        padding: "5px",
    }),
    singleValue: (provided, state) => {
        const opacity = state.isDisabled ? 0.5 : 1;
        const transition = "opacity 300ms";

        return { ...provided, opacity, transition, textAlign:'left' };
    },
    placeholder: (defaultStyles) => ({
        ...defaultStyles,
        textAlign:"left"
    }),
};

const Selectable = ({
    options,
    className,
    selectedOptions,
    setSelectedOptions,
    isSearchable=false,
}) => {

    return (
        <div style={{ width: "100%" }} className={className}>
            <Select
                value={selectedOptions}
                onChange={setSelectedOptions}
                options={options}
                styles={customStyles}
                isSearchable={isSearchable}
            />
        </div>
    );
};

export default Selectable;
