import { useState } from 'react';
import Select, { components } from 'react-select';
import './formChip.css'; // Import the CSS file

const YourComponent = () => {
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [inputValue, setInputValue] = useState('');

  const options = [
    { value: 'option1', label: 'Option 1' },
    { value: 'option2', label: 'Option 2' },
    { value: 'option3', label: 'Option 3' },
    // Add more options as needed
  ];

  const handleChange = (selected) => {
    setSelectedOptions(selected);
  };

  const handleInputChange = (inputValue) => {
    setInputValue(inputValue);
  };

  const handleRemoveOption = (optionValue) => {
    setSelectedOptions((prevSelected) =>
      prevSelected.filter((option) => option.value !== optionValue)
    );
  };

  // Custom rendering for the selected option in the Select dropdown value container
  const SingleValue = ({ children, ...props }) => {
    return (
      <components.SingleValue {...props}>
        {props.data.label}
      </components.SingleValue>
    );
  };

  // Custom option rendering to show the "cross" in the selected options container
  const Option = (props) => {
    return (
      <components.Option {...props}>
        {props.label}
        {props.isSelected && (
          <button
            onClick={() => handleRemoveOption(props.value)}
            className="remove-button"
          >
            &times;
          </button>
        )}
      </components.Option>
    );
  };

  return (
    <div>
      <div className="selected-options-container">
        {selectedOptions.map((option) => (
          <div key={option.value} className="selected-option">
            {option.label}
            <button
              onClick={() => handleRemoveOption(option.value)}
              className="remove-button"
            >
              &times;
            </button>
          </div>
        ))}
      </div>
      <Select
        options={options}
        value={selectedOptions}
        onChange={handleChange}
        onInputChange={handleInputChange}
        isMulti
        isClearable
        components={{
          Option: Option,
          SingleValue: SingleValue,
        }}
        formatOptionLabel={({ label }) => label}
      />
    </div>
  );
};

export default YourComponent;
