import { useState } from 'react';
import '../App.css';
import laptop from '../images/laptop.png';
import mobile from '../images/mobile.png';
import segment from '../images/segment.png';
import './header.scss';

function Header() {
    const [selectedTab, setSelectedTab] = useState('Assessment');

    const onTabClick = (tabOption) => {
      setSelectedTab(tabOption);
    };

    return (
    <div className='header'>
        <div className='headerBar'>
            <div className='headerTags'>
                <img src={segment} alt='burger icon'></img>
                <button className={`assessmentText ${selectedTab === 'Assessment' ? 'active' : ''}`}onClick={() => onTabClick('Assessment')}>
                    Assessment
                </button>
                <hr  className='divider'/>
                <div className='myAssessmentsText'className={` ${selectedTab === 'Stores' ? 'active' : ''}`}
      onClick={() => onTabClick('Stores')}>
                    My Assessments
                </div>
            </div>
            <img className='mobileIcon' src={mobile} alt='mobile icon' style={{ cursor: 'pointer' }}></img>
            <img src={laptop} alt='laptop icon' style={{ cursor: 'pointer' }}></img>
        </div>
    </div>
    );
}

export default Header;
