import { useState } from 'react';
import Select, { components } from 'react-select';
import './YourComponent.css'; // Import the CSS file

const YourComponent = () => {
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [inputValue, setInputValue] = useState('');

  const options = [
    { value: 'option1', label: 'Option 1' },
    { value: 'option2', label: 'Option 2' },
    { value: 'option3', label: 'Option 3' },
    // Add more options as needed
  ];

  const handleChange = (selected) => {
    setSelectedOptions(selected);
  };

  const handleInputChange = (inputValue) => {
    setInputValue(inputValue);
  };

  const handleRemoveOption = (optionValue) => {
    setSelectedOptions((prevSelected) =>
      prevSelected.filter((option) => option.value !== optionValue)
    );
  };

  // Custom rendering for the selected option in the Select dropdown value container
  const SingleValue = ({ children, ...props }) => {
    return <components.SingleValue {...props} />;
  };

  // Custom function to format the selected option label
  const formatOptionLabel = ({ label }) => label;

  // Custom dropdown indicator to hide the default input field
  const DropdownIndicator = () => {
    return null;
  };

  // Custom function to manually handle the inputValue for the Select component
  const handleSelectInputChange = (inputValue) => {
    setInputValue(inputValue);
  };

  // Custom function to manually handle the selection of an option for the Select component
  const handleSelectOption = (selectedOption) => {
    setInputValue('');
    setSelectedOptions((prevSelected) => [...prevSelected, selectedOption]);
  };

  return (
    <div>
      <div className="selected-options-container">
        {selectedOptions.map((option) => (
          <div key={option.value} className="selected-option">
            {option.label}
            <button
              onClick={() => handleRemoveOption(option.value)}
              className="remove-button"
            >
              &times;
            </button>
          </div>
        ))}
      </div>
      <Select
        options={options}
        value={null} // We set this to null to prevent showing selected options in the select input
        inputValue={inputValue} // Manually handle the inputValue for the Select component
        onInputChange={handleSelectInputChange} // Custom function to handle input changes
        onChange={handleSelectOption} // Custom function to handle option selection
        isMulti
        isClearable
        components={{
          SingleValue: SingleValue,
          DropdownIndicator: DropdownIndicator, // Use custom dropdown indicator
        }}
        formatOptionLabel={formatOptionLabel} // Use custom formatOptionLabel function
        styles={{
          // Hide the "cross" icon from the select input field
          clearIndicator: (base) => ({
            ...base,
            display: 'none',
          }),
        }}
      />
    </div>
  );
};

export default YourComponent;
